import React from 'react';
import './App.css';

import axios from 'axios';

// To add an object
class Add extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: [], text: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div>
        <h3>Objet a ajouter</h3>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="new-todo">
            Objet à ajouter
          </label>
          <input
            id="new-todo"
            onChange={this.handleChange}
            value={this.state.text}
          />
          <button className="waves-effect waves-light btn">
            Ajouter
          </button>
        </form>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.text.length) {
      return;
    }
    const newItem = {
      text: this.state.text,
    };
    this.setState(state => ({
      item: newItem,
      text: ''
    }));

    const objet = newItem

    axios.post('localhost:8080/shopping-list', { objet })
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
  }
}


// To show the objects
class Show extends React.Component {
  state = {
    objets : []
  }

  componentDidMount() {
    axios.get('localhost:8080/shopping-list')
      .then(res => {
        const obj = res.data;
        this.setState({ obj });
      })
  }

  render() {
    return (
      <div>
        <h3>Les objets</h3>
        <table>
          <thead>
            <tr>
                <th>Objets</th>
            </tr>
          </thead>
          <tbody>
              {this.state.objets.map(item => (
                <tr>item</tr>
              ))}
          </tbody>
        </table>
      </div>
    );
  }
}


// To show objects
class ShowOne extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: [], text: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div>
        <h3>Objet a ajouter</h3>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="new-todo">
            Objet à ajouter
          </label>
          <input
            id="new-todo"
            onChange={this.handleChange}
            value={this.state.text}
          />
          <button className="waves-effect waves-light btn">
            Ajouter
          </button>
        </form>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.text.length) {
      return;
    }
    const newItem = {
      text: this.state.text,
    };
    this.setState(state => ({
      item: newItem,
      text: ''
    }));
  }
}

function App() {
  return (
    <div className="App">
      <div className="container">
        <div className="row">
          <div className="col s12">
           <ul className="tabs">
              <li className="tab col s2"><a className="active" href="#test1">Ajouter objet</a></li>
              <li className="tab col s2"><a href="#test2">Voir les objets</a></li>
              <li className="tab col s2 "><a href="#test3">Voir un objet</a></li>
              <li className="tab col s2"><a href="#test4">Modifier un objet</a></li>
              <li className="tab col s2"><a href="#test5">Supprimer un objet</a></li>
            </ul>
          </div>
          <div id="test1" className="col s12"><Add/></div>
          <div id="test2" className="col s12"><Show/></div>
          <div id="test3" className="col s12">Test 3</div>
          <div id="test4" className="col s12">Test 4</div>
          <div id="test5" className="col s12">Test 5</div>
        </div>
      </div>
    </div>
  );
}

export default App;
